mapper.update:
UPDATE inquiry
SET answer=#{answer}, answer_date=NOW(), status='답변완료'
WHERE id=#{id}

UPDATE inquiry
SET status=#{status}
WHERE id=#{id}

mapper.select:
SELECT * FROM inquiry
WHERE category = #{category}
ORDER BY id DESC

SELECT * FROM inquiry
(title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%') OR writer LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY id DESC LIMIT #{offset}, #{pageSize}

SELECT * FROM inquiry
WHERE writer = #{writer}
ORDER BY id DESC
